// import React, { Component } from "react";
import React, { useState, useEffect } from "react";
import {
  API_KEY,
  API_URL,
  IMAGE_BASE_URL,
  POSTER_SIZE,
  BACKDROP_SIZE
} from "../../config";
import HeroImage from "../elements/HeroImage/HeroImage";
import SearchBar from "../elements/SearchBar/SearchBar";
import FourColGrid from "../elements/FourColGrid/FourColGrid";
import MovieThumb from "../elements/MovieThumb/MovieThumb";
import LoadMoreBtn from "../elements/LoadMoreBtn/LoadMoreBtn";
import Spinner from "../elements/Spinner/Spinner";
import "./Home.css";

// class Home extends Component {
//   state = {
//     movies: [],
//     heroImage: null,
//     loading: false,
//     currentPage: 0,
//     totalPages: 0,
//     searchTerm: ""
//   };

const Home = () => {
  const [state, setState] = useState({ movies: [] });
  const [isLoading, setIsloading] = useState(false);
  const [isError, setIsError] = useState(false);

  // componentDidMount() {
  //   if (localStorage.getItem("HomeState")) {
  //     const state = JSON.parse(localStorage.getItem("HomeState"));
  //     this.setState({ ...state });
  //   } else {
  //     this.setState({ loading: true });
  //     // const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
  //     this.fetchItems(this.createEndpoint("movie/popular", false, ""));
  //   }
  // }

  createEndpoint = (type, loadMore, searchTerm) => {
    return `${API_URL}${type}?api_key=${API_KEY}&page=${loadMore &&
      this.state.currentPage + 1}&query=${searchTerm}`;
  };

  updateItems = (loadMore, searchTerm) => {
    this.setState(
      {
        movies: loadMore ? [...this.state.movies] : [],
        loading: true,
        searchTerm: loadMore ? this.state.searchTerm : searchTerm
      },
      () => {
        this.fetchItems(
          !this.state.searchTerm
            ? this.createEndpoint("movie/popular", loadMore, "")
            : this.createEndpoint(
                "search/movie",
                loadMore,
                this.state.searchTerm
              )
        );
      }
    );
  };

  searchItems = searchTerm => {
    let endpoint = "";
    // this.setState({
    //   movies: [],
    //   loading: true,
    //   searchTerm
    // });

    if (!searchTerm) {
      endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
    } else {
      endpoint = `${API_URL}search/movie?api_key=${API_KEY}&language=en-US&query=${searchTerm}`;
    }

    this.fetchMovies(endpoint);
  };

  loadMoreItems = () => {
    let endpoint = "";
    // this.setState({ loading: true });
    if (!this.state.searchTerm) {
      endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=${this
        .state.currentPage + 1}`;
    } else {
      endpoint = `${API_URL}search/movie?api_key=${API_KEY}&langusge=en-US&query=${
        this.state.searchTerm
      }&page=${this.state.currentPage + 1}`;
    }

    this.fetchMovies(endpoint);
  };

  // fetchItems = async endpoint => {
  //   const result = await (await fetch(endpoint)).json();
  //   try {
  //     this.setState(
  //       {
  //         movies: [...this.state.movies, ...result.results],
  //         heroImage: this.state.heroImage || result.results[6],
  //         loading: false,
  //         currentPage: result.page,
  //         totalPages: result.total_pages
  //       },
  //       () => {
  //         if (this.state.searchTerm === "") {
  //           localStorage.setItem("HomeState", JSON.stringify(this.state));
  //         }
  //       }
  //     );
  //   } catch (e) {
  //     console.log("There was an error:", e);
  //   }
  // };

  // fetchItems = endpoint => {
  //   fetch(endpoint)
  //     .then(result => result.json())
  //     .then(result =>
  //       this.setState(
  //         {
  //           movies: [...this.state.movies, ...result.results],
  //           heroImage: this.state.heroImage || result.results[6],
  //           loading: false,
  //           currentPage: result.page,
  //           totalPages: result.total_pages
  //         },
  //         () => {
  //           if (this.state.searchTerm === "") {
  //             localStorage.setItem("HomeState", JSON.stringify(this.state));
  //           }
  //         }
  //       )
  //     );
  // };

  return (
    // ES6 desctructuring the state
    <div className="rmdb-home">
      {heroImage && !searchTerm ? (
        <div>
          <HeroImage
            image={`${IMAGE_BASE_URL}${BACKDROP_SIZE}${state.heroImage.backdrop_path}`}
            title={state.heroImage.original_title}
            text={state.heroImage.overview}
          />
        </div>
      ) : null}
      <SearchBar callback={updateItems} />
      <div className="rmdb-home-grid">
        <FourColGrid
          header={state.searchTerm ? "Search Result" : "Popular Movies"}
          loading={isLoading}
        >
          {state.movies.map((element, i) => {
            return (
              <MovieThumb
                key={i}
                clickable={true}
                image={
                  element.poster_path
                    ? `${IMAGE_BASE_URL}${POSTER_SIZE}${element.poster_path}`
                    : "./images/no_image.jpg"
                }
                movieId={element.id}
                movieName={element.original_title}
              />
            );
          })}
        </FourColGrid>
        {isLoading ? <Spinner /> : null}
        {state.currentPage < state.totalPages && !isLoading ? (
          <LoadMoreBtn onClick={updateItems} text="Load More" />
        ) : null}
      </div>
    </div>
  );
};

export default Home;
